mapper.select:
SELECT * FROM Car_Model_Lv1 WHERE flagUseable = 'yes' and CarBrand = #{carBrand}
and carSubBrand = #{carSubBrand}
ORDER BY orderIndex

SELECT Car_Model_Lv1.id as carModelLv1, Car_Brand.cname as brandCname, Car_Model_Lv1.cname as cname, Car_Model_Lv1.imgPath as imgPath, min(Car_Model_Lv3.factoryPrice) as factoryPriceMin, max(Car_Model_Lv3.factoryPrice) as factoryPriceMax, min(Shop_Stock.carPrice) as shopPriceMin, max(Shop_Stock.carPrice) as shopPriceMax FROM (select * from Car_Model_Lv1 where flagUseable = 'yes') Car_Model_Lv1 LEFT JOIN Car_Model_Lv2 ON Car_Model_Lv1.id = Car_Model_Lv2.carModelLv1 AND Car_Model_Lv2.flagUseable = 'yes' LEFT JOIN Car_Model_Lv3 ON Car_Model_Lv2.id = Car_Model_Lv3.carModelLv2 AND Car_Model_Lv3.flagUseable = 'yes' LEFT JOIN Car_Brand ON Car_Model_Lv1.carBrand = Car_Brand.id AND Car_Brand.flagUseable = 'yes' LEFT JOIN Shop_Stock ON Car_Model_Lv3.id = Shop_Stock.carModelLv3 LEFT JOIN Shop ON Shop_Stock.shop = Shop.id AND Shop.flagUseable = 'yes'
Car_Brand.id = #{carBrand}
AND Car_Model_Lv1.id = #{carModelLv1}
AND ( Car_Model_Lv3.factoryPrice >= #{priceMin} or Shop_Stock.carPrice >= #{priceMin} )
AND ( Car_Model_Lv3.factoryPrice <= #{priceMax} or Shop_Stock.carPrice <= #{priceMax} )
AND Shop.shopCity = #{city}
AND Shop.id = #{shop}
GROUP BY Car_Model_Lv1.cname , Car_Model_Lv1.imgPath , Car_Brand.cname
ORDER BY shopPriceMin asc
ORDER BY shopPriceMax desc
LIMIT #{startIndex},#{pageSize}

		SELECT 
		    count(distinct Car_Model_Lv1.cname , Car_Model_Lv1.imgPath , Car_Brand.cname)
		FROM
		    (select * from Car_Model_Lv1 where flagUseable = 'yes') Car_Model_Lv1
		        LEFT JOIN
		    Car_Model_Lv2 ON Car_Model_Lv1.id = Car_Model_Lv2.carModelLv1
				AND Car_Model_Lv2.flagUseable = 'yes'
		        LEFT JOIN
		    Car_Model_Lv3 ON Car_Model_Lv2.id = Car_Model_Lv3.carModelLv2
				AND Car_Model_Lv3.flagUseable = 'yes'
		        LEFT JOIN
		    Car_Brand ON Car_Model_Lv1.carBrand = Car_Brand.id
				AND Car_Brand.flagUseable = 'yes'
		        LEFT JOIN
		    Shop_Stock ON Car_Model_Lv3.id = Shop_Stock.carModelLv3
		    	LEFT JOIN
		    Shop ON Shop_Stock.shop = Shop.id
		    AND Shop.flagUseable = 'yes'


                Car_Brand.id = #{carBrand}


                AND Car_Model_Lv1.id = #{carModelLv1}


                AND (
                  Car_Model_Lv3.factoryPrice >= #{priceMin}
                 or 
                  Shop_Stock.carPrice >=  #{priceMin}
                )


                AND (
                  Car_Model_Lv3.factoryPrice  <= #{priceMax}
                 or 
                  Shop_Stock.carPrice  <=  #{priceMax}
                )


                AND Shop.shopCity = #{city}


                AND Shop.id = #{shop}
